<?php/** * Plugin Name: nBlocks - Responsive Gutenberg News Blocks * Plugin URI: https://wordpress.org/plugins/nblocks/ * Description: nBlocks is a Gutenberg compatible plugin that provides multiple responsive block elements for displaying WordPress posts. With this plugin, you can easily filter posts by author, date, category, and sort them using various variables. * Author: officialprocoders * Version: 1.0.2 * Text Domain: n-blocks * License: GPLv2 * License URI: https://www.gnu.org/licenses/gpl-2.0.html */require plugin_dir_path( __FILE__ ) . 'blocks/post-filter/index.php';/** * Filter to Allow tag in wp_kses_post function. * * @param  array  $html * @param  string $context * @return array */function npub_wp_kses_allowed_html( $html, $context ) {	$html['img']['srcset'] = 1;	$html['svg']           = array(		'class'           => true,		'aria-hidden'     => true,		'aria-labelledby' => true,		'role'            => true,		'xmlns'           => true,		'width'           => true,		'height'          => true,		'viewbox'         => true, // <= Must be lower case!	);	$html['path']          = array(		'd'    => true,		'fill' => true,	);	return $html;}add_filter( 'wp_kses_allowed_html', 'npub_wp_kses_allowed_html', 10, 2 );